// src/components/Calculator.js
import React, { useState } from 'react';
import axios from 'axios';
import { TextField, Button, Typography, Container } from '@mui/material';

const Calculator = () => {
    const [input, setInput] = useState('');
    const [result, setResult] = useState(null);
    const [error, setError] = useState('');

    const handleCalculate = async () => {
        setError('');
        try {
            const response = await axios.post('http://localhost:5000/api/calculate', { numbers: input });
            setResult(response.data.result);
        } catch (err) {
            setError(err.response.data.error || 'An error occurred');
        }
    };

    return (
        <Container maxWidth="sm" style={{ marginTop: '2rem' }}>
            <Typography variant="h4" gutterBottom align="center">String Calculator</Typography>
            <TextField
                label="Enter numbers"
                variant="outlined"
                fullWidth
                value={input}
                onChange={(e) => setInput(e.target.value)}
                style={{ marginBottom: '1rem' }}
            />
            <Button variant="contained" color="primary" onClick={handleCalculate} fullWidth>
                Calculate
            </Button>
            {result !== null && <Typography variant="h6" color="green" align="center" style={{ marginTop: '1rem' }}>Result: {result}</Typography>}
            {error && <Typography variant="h6" color="red" align="center" style={{ marginTop: '1rem' }}>{error}</Typography>}
        </Container>
    );
};

export default Calculator;
